import React from "react";
import "./form.css";

const FormSection = () => {
  return (
    <>
      <div className="form-container">
        <div className="faq">
          <div className="box">
            <div className="bar"></div>
            <span>FAQ</span>
          </div>
          <h2>Frequently asked questions</h2>
          <Expandtoggle
            function={click1()}
            btnText={"What are the Benifits of Business"}
            togglePara={
              "Lorem ipsum is placeholder text commonly used to demonstrate the visual form of a document. used to demonstrate the visual form of a document."
            }
          />
        </div>
        <div className="form">
          <div className="form-head">
            <h3>Free Business Consultant</h3>
            <p>24/7 Anytime open our service</p>
          </div>
          <div className="form-entries">
            <form action="">
              <Input type={"text"} placeholder={"YOUR NAME*"} />
              <Input type={"email"} placeholder={"YOUR E-MAIL*"} />
              <Input type={"tel"} placeholder={"YOUR NUMBER*"} />
              <textarea
                className="text-area"
                placeholder="WRITE MESSAGE*"
              ></textarea>
              <Input type={"submit"} value={"FREE CONSULTANT"} />
            </form>
          </div>
        </div>
      </div>
      <div className="arrow4"></div>
    </>
  );
};
export default FormSection;

function Expandtoggle(props) {
  return (
    <>
      <div className="toggle-container">
        <button className="toggle-expand" onClick={props.function}>
          <div className="toggle-img"></div>
          <span>{props.btnText}</span>
          <div className="plus-minus">
            <i class="fa-solid fa-plus"></i>
          </div>
        </button>
        <p id={props.paraID}>{props.togglePara}</p>
      </div>
    </>
  );
}
