import React from "react";
import "./about.css";

function Circle() {
  return (
    <div>
      <div class="contain">
        <div class="circular-progress">
          <span class="progress-value">0%</span>
        </div>
      </div>
    </div>
  );
}

const Main = () => {
  return (
    <>
      <div className="about">
        <div className="about-top">
          <p>Don't be confused we advise you</p>
          <button className="contact-btn">Contact Now</button>
        </div>
        <div className="about-bottom">
          <div className="bott-box">
            <div className="box">
              <div className="bar"></div>
              <span>About ZUIZ Business Services</span>
            </div>
            <div className="box">
              <h1>
                We are more than <br /> you imagine
              </h1>
            </div>
            <div className="box">
              <p>
                Lorem ipsum is placeholder text commonly used to demonstrate the
                visual form of a document.
              </p>
            </div>
            <div className="colum">
              <div className="arrow">
                <div className="left"></div>
                <div className="right">
                  <p>Lorem ipsum is a placeholder.</p>
                </div>
              </div>
              <div className="arrow">
                <div className="left"></div>
                <div className="right">
                  <p>Lorem ipsum is a placeholder.</p>
                </div>
              </div>
              <div className="box">
                <Circle />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Main;

let circularProgress = document.querySelector(".circular-progress"),
  progressValue = document.querySelector(".progress-value");

let progressStartValue = 0,
  progressEndValue = 50,
  speed = 10;

let progress = setInterval(() => {
  progressStartValue++;

  progressValue.textContent = `${progressStartValue}%`;
  circularProgress.style.background = `conic-gradient(var(--red) ${
    progressStartValue * 3.6
  }deg, #ededed 0deg)`;

  if (progressStartValue === progressEndValue) {
    clearInterval(progress);
  }
}, speed);
